import React from "react";

export function TodoItem({item,onCheck}:any) {
  const handleChange = () => {
    onCheck(item);
    console.log('クリックされました！')
  };

  return (
    <label className="panel-block">
      <input 
        type="checkbox"
        checked={item.done}
        onChange={handleChange}
      />
      <span
    className={classNames({
      'has-text-grey-light': item.done
    })}
  ></span>
      {item.text}
    </label>
  );
}
